mapper.select:
SELECT
r.brand_name, r.account_money, r.accounting, m.section_name, d.name, d.weight, d.price, w.first_name, w.last_name, w.position, w.age, w.experience, wa.country as "worker_country", wa.city as "worker_city", wa.street as "worker_street", wa.house as "worker_house", wa.apartment as "worker_apartment", a.country as "restaurant_country", a.city as "restaurant_city", a.street as "restaurant_street", a.house as "restaurant_house", a.apartment as "restaurant_apartment"
FROM restaurants r INNER JOIN menus m ON m.restaurant_id = r.id INNER JOIN dishes d on m.dish_id = d.id INNER JOIN restaurant_workers rw on r.id = rw.restaurant_id INNER JOIN workers w on rw.worker_id = w.id INNER JOIN addresses a on r.address_id = a.id INNER JOIN addresses wa on wa.id = w.address_id;
